import React, { Component } from 'react';
import {connect} from ''

class Post extends Component{
    render(){
        const {posts} = this.props;
        const arr = posts ? (
            <div className="post">
                <div className="card-title">{posts.title}</div>
                <p>{posts.body}</p>
            </div>
        ): (
            <div className="center">Loading Post...</div>
        )
        return(
            <div>
                {arr}
            </div>
        );
    }
}

const mapsStateToProps = (state, ownProp) => {
    let id = ownProp.match.params.posts_id;
    return{
        post: state.posts.find(res=> res.id == id)
    }
}
export default Post;
