import React, { Component } from 'react';
import {connect} from 'react-redux'

class Post extends Component{
    handleClick = () => {
        this.props.deletePost(this.props.posts.id);
    }
    render(){
        const {posts} = this.props;
        const arr = posts ? (
            <div className="post card">
                <div className="card-title">{posts.title}</div>
                <p>{posts.body}</p>
                <div className="center">
                <button className="btn red" onClick={this.handleClick}>DELETE</button>
                </div>
            </div>
        ): (
            <div className="center">Loading Post...</div>
        )
        return(
            <div className="container">
                {arr}
            </div>
        );
    }
}

const mapsStateToProps = (state, ownProp) => {
    let id = ownProp.match.params.posts_id;
    return{
        posts: state.posts.find(res=> res.id === id)
    }
}
const mapsDispatchToPros = (dispatch) => {
    return {
        deletePost : (id) => {dispatch({type: "DELETE_POST", id:id})}
    }
}
export default connect(mapsStateToProps, mapsDispatchToPros)(Post);
