import React, { Component } from 'react';
import axios from 'axios';


class Post extends Component{
    state = {
        id: null
    }
    componentDidMount(){
        let id = this.props.match.params.posts_id;
        axios.get('https://jsonplaceholder.typicode.com/posts' + id);
        this.setState({
            id
        })   
    }
    render(){
        return(
            <div>
                <h1> {this.state.id}</h1>
            </div>
        );
    }
}
export default Post;
